pragma solidity ^0.4.13;

import './LocCoin.sol';
import 'zeppelin-solidity/contracts/crowdsale/Crowdsale.sol';


//3 t crowdfund, mi t 2 ngy, khong cch gia mi t l 2 ngy. 
//S confirm minimum l 6. 
//Tng s token sale: 240 tr token. ~ 10 tr eth. 
//t u: 50 tr. t l: 1/50. Max: 1 tr eth. Chuyn trong 2 ngy.
//t 2: 90tr. 1/25. Max: 3.6 eth. Sau khi ht 1 tun
//t cui: 100tr. 1/10. Max 10 eth. Sau khi ht 1 tun.

//Testnet: tnh = gi
//M: RockshipCoin

contract LocCoinCrowdsale is Crowdsale {
    enum Stages {
        FirstStage,
        FirstBreak,
        MiddleStage,
        LastBreak,
        LastStage
    }

    function LocCoinCrowdsale(uint256 _startTime, uint256 _endTime, uint256 _rate, address _wallet) public 
        Crowdsale(_startTime, _endTime, _rate, _wallet) {          
    }

    // This is the current stage.
    Stages public stage = Stages.FirstStage;

    uint public creationTime = now;

    modifier atStage(Stages _stage) {
        require(stage == _stage);
        _;
    }

    function nextStage() internal {
        stage = Stages(uint(stage) + 1);
    }

    // Perform timed transitions. Be sure to mention
    // this modifier first, otherwise the guards
    // will not take the new stage into account.
    modifier timedTransitions() {
        if (stage == Stages.FirstStage &&
                    now >= creationTime + 2 days)
            nextStage();
        if (stage == Stages.FirstBreak &&
                now >= creationTime + 4 days)
            nextStage();
        if (stage == Stages.FirstBreak &&
                now >= creationTime + 6 days)
            nextStage();
        if (stage == Stages.FirstBreak &&
                now >= creationTime + 8 days)
            nextStage();
        if (stage == Stages.FirstBreak &&
                now >= creationTime + 10 days)
            nextStage();
        // The other stages transition by transaction
        _;
    }

    // Order of the modifiers matters here!
    function bid() public
        payable
        timedTransitions
        atStage(Stages.FirstStage)
    {
        // We will not implement that here
    }

    function reveal() public
        timedTransitions
        atStage(Stages.FirstBreak)
    {
    }

    // This modifier goes to the next stage
    // after the function is done.
    modifier transitionNext()
    {
        _;
        nextStage();
    }

    function g() public
        timedTransitions
        atStage(Stages.MiddleStage)
        transitionNext
    {
    }

    function h() public
        timedTransitions
        atStage(Stages.LastBreak)
        transitionNext
    {
    }

    function i() public
        timedTransitions
        atStage(Stages.LastStage)
    {
    }

    function createTokenContract() internal returns (MintableToken) {
        return new LocCoin();
    }
}
